mapper.select:
select * from Rent_equipment

    rid = #{rid}
    and eid = #{eid}
    and eName = #{eName}
    and uid = #{uid}
    and username = #{username}
    and rentTime = #{rentTime}
    and number = #{number}

SELECT SUM(rentTime*ers.`price`*number)
FROM Rent_equipment re INNER JOIN Equipment_rent_standard ers
ON  ers.`eid` = re.`eid`

    YEAR(rentDate) = #{year} 
    and MONTH(rentDate) = #{month}
